import React from 'react';
import ReactApexChart from "react-apexcharts";
import { lineChartOptions } from './config';

const Line = ({height}) => {
    //라인 옵션
    const [lineOption, setLineOption] = React.useState(lineChartOptions);
    //서버에 보낼 파라미터
    const [resParam, setResParam] = React.useState({
        FromDate : "2021-05-01",
        ToDate : "2021-05-10",
        Category1 : "패션의류",
        Category2 : "여성의류",
        Category3 : "티셔츠",
        Keyword : "켈린클라인"
    });
    //서버 호출 후 받는 데이터
    const [resData, setResData] = React.useState({
        TrendData: [
            { date: '2019-05-01', Value: 0.2 },
            { date: '2019-05-02', Value: 0.3 },
            { date: '2019-05-03', Value: 0.4 },
            { date: '2019-05-04', Value: 0.5 },
            { date: '2019-05-05', Value: 0.2 },
            { date: '2019-05-06', Value: 0.9 },
            { date: '2019-05-07', Value: 0.3 },
            { date: '2019-05-08', Value: 0.2 },
            { date: '2019-05-09', Value: 0.6 },
            { date: '2019-05-10', Value: 0.3 }
        ]
    });

    React.useEffect(() => {
        var data = [];
        var category = [];

        resData.TrendData.map((res) => {
            data.push(res.Value);
            category.push(res.date);
        });

        console.log(data);
        console.log(category);
        
        setLineOption({
            series: data,
            options: {
                ...lineOption.options,
                
                color : {
                    ...lineOption.options.colors
                },
                dataLabels : {
                    ...lineOption.options.dataLabels
                },
                stroke : {
                    ...lineOption.options.stroke
                },
                title : {
                    ...lineOption.options.title,
                    text : 'PostTrend'
                },
                grid : {
                    ...lineOption.options.grid
                },
                markers : {
                    ...lineOption.options.markers
                },
                xaxis : {
                    categories : category,
                    title: {
                        text: 'Value'
                    }
                },
                yaxis: {
                    ...lineOption.yaxis
                },
                legend: {
                    ...lineOption.legend
                }
            }
        });
    }, []);

    return (
        <ReactApexChart options={lineOption} series={lineChartOptions.series} type="line" height={height} />
    );
};

export default Line;
