import React from 'react';
import ReactApexChart from "react-apexcharts";
import { lineChartOptions } from './config';

const Line = ({height}) => {
    //라인 옵션
    const [lineOption, setLineOption] = React.useState(lineChartOptions);
    //서버에 보낼 파라미터
    const [resParam, setResParam] = React.useState({
        FromDate : "2021-05-01",
        ToDate : "2021-05-10",
        Category1 : "패션의류",
        Category2 : "여성의류",
        Category3 : "티셔츠",
        Keyword : "켈린클라인"
    });
    //서버 호출 후 받는 데이터
    const [resData, setResData] = React.useState({
        TrendData: [
            { date: '2019-05-01', Value: 0.2 },
            { date: '2019-05-02', Value: 0.3 },
            { date: '2019-05-03', Value: 0.4 },
            { date: '2019-05-04', Value: 0.5 },
            { date: '2019-05-05', Value: 0.2 },
            { date: '2019-05-06', Value: 0.9 },
            { date: '2019-05-07', Value: 0.3 },
            { date: '2019-05-08', Value: 0.2 },
            { date: '2019-05-09', Value: 0.6 },
            { date: '2019-05-10', Value: 0.3 }
        ]
    });

    /*
        series: [
              {
                name: "High - 2013",
                data: [28, 29, 33, 36, 32, 32, 33]
              },
              {
                name: "Low - 2013",
                data: [12, 11, 14, 18, 17, 13, 13]
              }
        ],
        options: {
              chart: {
                height: 350,
                type: 'line',
                dropShadow: {
                  enabled: true,
                  color: '#000',
                  top: 18,
                  left: 7,
                  blur: 10,
                  opacity: 0.2
                },
                toolbar: {
                  show: false
                }
              },
              colors: ['#77B6EA', '#545454'],
        dataLabels: {
                enabled: true,
              },
              stroke: {
                curve: 'smooth'
              },
              title: {
                text: 'Average High & Low Temperature',
                align: 'left'
              },
              grid: {
                borderColor: '#e7e7e7',
                row: {
                  colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
                  opacity: 0.5
                },
              },
              markers: {
                size: 1
              },
              xaxis: {
                categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
                title: {
                  text: 'Month'
                }
              },
              yaxis: {
                title: {
                  text: 'Temperature'
                },
                min: 5,
                max: 40
              },
              legend: {
                position: 'top',
                horizontalAlign: 'right',
                floating: true,
                offsetY: -25,
                offsetX: -5
              }
            },
          };
    */

    React.useEffect(() => {
        var category = [];
        
        resData.TrendData.map((res) => {
            category.push(res.date);
        });
        

    }, []);

    return (
        <ReactApexChart options={lineChartOptions.options} series={lineChartOptions.series} type="line" height={height} />
    );
};

export default Line;
